    private void ingresarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_ingresarActionPerformed

        int b = 0;
        
        AudioClip sonido;
        sonido = java.applet.Applet.newAudioClip(getClass().getResource("/evidencia2/assets/sectionpass.wav"));
        sonido.play();
        
        if (dato.getText().length() > 0) {
            try {
                b = Integer.parseInt(dato.getText());
            } catch (NumberFormatException e) {
                JOptionPane.showMessageDialog(this, "El valor no es un numero");
            }

            if (b != 0) {
                a = new Nodo(b);

                if (flag == true) {
                    ab = new ArbolBinario(Integer.valueOf(dato.getText()));
                    flag = false;
                } else {
                    ab.Add(a, ab.raiz);
                }
                JOptionPane.showMessageDialog(this, "Dato agregado");
            }

        } else {
            JOptionPane.showMessageDialog(this, "El campo esta vacio");
        }

        dato.setText("");
        
    }//GEN-LAST:event_ingresarActionPerformed

