    private void eliminarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_eliminarActionPerformed
        AudioClip sonido;
        sonido = java.applet.Applet.newAudioClip(getClass().getResource("/evidencia2/assets/sectionpass.wav"));
        sonido.play();
        int a = 0;
        if (dato.getText().length() > 0) {
            try {
                a = Integer.parseInt(dato.getText());
            } catch (NumberFormatException e) {
                JOptionPane.showMessageDialog(this, "El valor no es un numero");
            }
            if (a != 0) {
                ab.borrar(a);
            }
        } else {
            JOptionPane.showMessageDialog(this, "El campo esta vacio");
        }

        dato.setText("");
    }//GEN-LAST:event_eliminarActionPerformed

    private void buscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buscarActionPerformed

        AudioClip sonido;
        sonido = java.applet.Applet.newAudioClip(getClass().getResource("/evidencia2/assets/sectionpass.wav"));
        sonido.play();
        int a = 0;
        if (dato.getText().length() > 0) {
            try {
                a = Integer.parseInt(dato.getText());
            } catch (NumberFormatException e) {
                JOptionPane.showMessageDialog(this, "El valor no es un numero");
            }
            if (a != 0) {
                boolean c = ab.buscar(a);
                dato.setText("");

                if (c == true) {
                    area.setText("Dato: " + a + " si existe");
                } else {
                    area.setText("Dato: " + a + " no existe");
                }
            }
        } else {
            JOptionPane.showMessageDialog(this, "El campo esta vacio");
        }

        dato.setText("");



    }//GEN-LAST:event_buscarActionPerformed

